import React, { Component } from "react";
import "../css/home.css";
import { Row, Col, Container } from 'react-bootstrap';
import UserService from "../services/UserService.js";
import PostService from "../services/PostService.js";
import "../css/post.css";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: []
    };
    this.tag = {
      content: [],
    }
  }


  componentDidMount() {
    UserService.getPublicContent().then(
      response => {
        this.setState({
          content: response.data
        });
        PostService.getTopTags().then(
          response => {
            this.tag.content = response.data;
          }
        );
      },
      error => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString()
        });
      }
    );
  }
  getTags = () => {
    PostService.getTopTags().then(
      response => {
        this.tag.content = response.data;
      },
      error => {
        this.tag.content =
          (error.response && error.response.data) ||
          error.message ||
          error.toString()
      }
    );
  }
  handlePost = (slug) => {
    PostService.getSlug(slug).then(response => {
      alert(response.data.content);
    })
  }
  render() {
    console.log(this.state.content);
    return (
      <Container>
        <Row>
          <Col sm={9}>
            {this.state.content.map(item => {
              return <div class="home-container">
              <div class="course">
                <div class="course-preview">
                <img src={item.thumbnail} alt="none" class="img-fluid"></img>
                </div>
                <div class="course-info">
                  <h3>{item.title}</h3>
                  <h6>{item.brief}</h6>
                  <h6>{item.createdAt}</h6>
                  <button class="btn" onClick={() => this.handlePost(item.slug)}>Read</button>
                </div>
              </div>
            </div>
            })}
          </Col>
          <Col sm={3}>
          {this.tag.content.map(item => {
              return <div class="home-container">
              <div class="course">
                <div class="course-info">
                  <h6>{item.name}</h6>
                </div>
              </div>
            </div>
            })}
          </Col>
        </Row>
      </Container>
    );
  }
}
