import React, { Component } from "react";
import { Row, Col, Container } from 'react-bootstrap';
import UserService from "../services/UserService";
import EventBus from "../common/EventBus";
//import PostService from "../services/PostService.js";
import "../css/post.css";
export default class BoardUser extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: []
    };
  }

  componentDidMount() {
    UserService.getUserBoard(localStorage.user_id).then(
      response => {
        this.setState({
          content: response.data
        });
      },
      error => {
        this.setState({
          content:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });

        if (error.response && error.response.status === 401) {
          EventBus.dispatch("logout");
        }
      }
    );
  }

  render() {
    return (
      <Container>
        <Row>
          <Col sm={9}>
            {this.state.content.map(item => {
              return  <div  key={item.id} class="home-container" >
              <div class="row">
                <div class="column-33">
                <div className="thumbnail">{item.thumbnail}</div>
                </div>
                <div class="column-66">
                  <h1 class="xlarge-font"><b>{item.title}</b></h1>
                  <p><span>{item.brief}</span></p>
                  <p><span>Create At:{item.createdAt}</span></p>
                  <button onClick={() => this.handlePost(item.slug)}>Read</button>
                </div>
              </div>
            </div>
            })}
          </Col>
          <Col sm={3}>RIGHT SIDE</Col>
        </Row>
      </Container>
    );
  }
}
