import React, { Component } from "react";
import "../css/home.css";
import { Row, Col, Container } from 'react-bootstrap';
import UserService from "../services/UserService.js";
import PostService from "../services/PostService.js";
import "../css/post.css";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: []
    };
  }

  componentDidMount() {
    UserService.getPublicContent().then(
      response => {
        this.setState({
          content: response.data
        });
      },
      error => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString()
        });
      }
    );
  }
  handlePost = (slug) => {
    PostService.getSlug(slug).then(() => { alert('ok') })
  }
  render() {
    console.log(this.state.content);
    return (
      <Container>
        <Row>
          <Col sm={9}>
            {this.state.content.map(item => {
              return <div class="mdl-layout mdl-js-layout mdl-color--grey-100">
              <main class="mdl-layout__content">
                <div class="mdl-grid">
                  <div class="mdl-card mdl-cell mdl-cell--6-col mdl-cell--4-col-tablet mdl-shadow--2dp">
                    <figure class="mdl-card__media">
                      <img src="https://tfirdaus.github.io/mdl/images/laptop.jpg" alt="" />
                    </figure>
                    <div class="mdl-card__title">
                      <h1 class="mdl-card__title-text">Learning Web Design</h1>
                    </div>
                    <div class="mdl-card__supporting-text">
                      <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aliquam accusamus, consectetur.</p>
                    </div>
                            <div class="mdl-card__actions mdl-card--border">
                <a class="mdl-button mdl-button--colored mdl-js-button mdl-js-ripple-effect">Read More</a>
                <div class="mdl-layout-spacer"></div>
                  <button class="mdl-button mdl-button--icon mdl-button--colored"><i class="material-icons">favorite</i></button>
                      <button class="mdl-button mdl-button--icon mdl-button--colored"><i class="material-icons">share</i></button>
                </div>
                  </div>



                  <div class="mdl-card mdl-cell mdl-cell--6-col mdl-cell--4-col-tablet mdl-shadow--2dp">
                    <figure class="mdl-card__media">
                      <img src="https://tfirdaus.github.io/mdl/images/html5.jpg" alt="" />
                    </figure>
                    <div class="mdl-card__title">
                      <h1 class="mdl-card__title-text">Learning HTML</h1>
                    </div>
                    <div class="mdl-card__supporting-text">
                      <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aliquam accusamus, consectetur.</p>
                    </div>
                    <div class="mdl-card__actions mdl-card--border">
                <button class="mdl-button mdl-button--colored mdl-js-button mdl-js-ripple-effect">Read More</button>
                <div class="mdl-layout-spacer"></div>
                  <button class="mdl-button mdl-button--icon mdl-button--colored"><i class="material-icons">favorite</i></button>
                      <button class="mdl-button mdl-button--icon mdl-button--colored"><i class="material-icons">share</i></button>
                </div>
                  </div>
                </div>
              </main>
            </div>
              /*
              return <div key={item.id} class="home-container" >
              <div class="row">
                <div class="column-33">
                <div className="thumbnail">{item.thumbnail}</div>
                </div>
                <div class="column-66">
                  <h1 class="xlarge-font"><b>{item.title}</b></h1>
                  <p><span>{item.brief}</span></p>
                  <p><span>Create At:{item.createdAt}</span></p>
                  <button onClick={() => this.handlePost(item.slug)}>Read</button>
                </div>
              </div>
            </div>*/
            })}
          </Col>
          <Col sm={3}>RIGHT SIDE</Col>
        </Row>
      </Container>
    );
  }
}
