import React, { Component } from "react";
import "../css/home.css";
import { Row, Col, Container } from 'react-bootstrap';
import UserService from "../services/UserService.js";
import PostService from "../services/PostService.js";
import "../css/post.css";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: []
    };
  }

  componentDidMount() {
    UserService.getPublicContent().then(
      response => {
        this.setState({
          content: response.data
        });
      },
      error => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString()
        });
      }
    );
  }
  handlePost = (slug) => {
    PostService.getSlug(slug).then(() => { alert('ok') })
  }
  render() {
    console.log(this.state.content);
    return (
      <Container>
        <Row>
          <Col sm={9}>
            {this.state.content.map(item => {
              return <div class="home-container">
              <div class="course">
                <div class="course-preview">
                <div className="thumbnail">{item.thumbnail}</div>
                </div>
                <div class="course-info">
                  <h2>{item.title}</h2>
                  <h6>{item.brief}</h6>
                  <h6>{item.createdAt}</h6>
                  <button class="btn" onClick={() => this.handlePost(item.slug)}>Read</button>
                </div>
              </div>
            </div>
              /*
              return <div key={item.id} class="home-container" >
              <div class="row">
                <div class="column-33">
                <div className="thumbnail">{item.thumbnail}</div>
                </div>
                <div class="column-66">
                  <h1 class="xlarge-font"><b>{item.title}</b></h1>
                  <p><span>{item.brief}</span></p>
                  <p><span>Create At:{item.createdAt}</span></p>
                  <button onClick={() => this.handlePost(item.slug)}>Read</button>
                </div>
              </div>
            </div>*/
            })}
          </Col>
          <Col sm={3}>RIGHT SIDE</Col>
        </Row>
      </Container>
    );
  }
}
